@section('footer-scripts')
    <script>
        document.getElementsByClassName("license-agreement")[0].addEventListener("scroll", checkAgreementHeightOnScroll, false);
        function checkAgreementHeightOnScroll(){
            var agreement = $('.license-agreement')[0];
            if ((agreement.scrollTop + agreement.offsetHeight) >= agreement.scrollHeight){
                $('.acept-agreement-box').css('display', 'flex');
            }
        }
        $('span.acept-agreements').click(function(){
            $('.run-installation').html('<a href="{{ route('einstall-step-1') }}" class="button button-large">Start Installation</a>');
        });
    </script>
